import React, {Component} from 'react';
import {View, Text} from 'react-native';
import {xdateToData} from '../../interface';
import XDate from 'xdate';
import dateutils from '../../dateutils';
import styleConstructor from './style';

class ReservationListItem extends Component {
  constructor(props) {
    super(props);
    this.styles = styleConstructor(props.theme);
  }

  shouldComponentUpdate(nextProps) {
    const r1 = this.props.item;
    const r2 = nextProps.item;
    let changed = true;
    if (!r1 && !r2) {
      changed = false;
    } else if (r1 && r2) {
      if (r1.day.getTime() !== r2.day.getTime()) {
        changed = true;
      } else if (!r1.reservation && !r2.reservation) {
        changed = false;
      } else if (r1.reservation && r2.reservation) {
        if ((!r1.date && !r2.date) || (r1.date && r2.date)) {
          changed = this.props.rowHasChanged(r1.reservation, r2.reservation);
        }
      }
    }
    return changed;
  }

  renderTitle(date, item) {
    if (this.props.renderDay) {
      return this.props.renderDay(date ? xdateToData(date) : undefined, item);
    }
    const today = dateutils.sameDate(date, XDate()) ? this.styles.today : undefined;
    if (date) {
      return (
        <View style={this.styles.day}>
          <Text style={[this.styles.dayText, today]}>
            {XDate.locales[XDate.defaultLocale].dayNames[date.getDay()]}, {XDate.locales[XDate.defaultLocale].monthNames[date.getMonth()]} {date.getDate()}
          </Text>
        </View>
      );
    } else {
      return (
        <View style={this.styles.day}/>
      );
    }
  }

  render() { // toheeb if no content here, then don't show date
    const {reservation, date} = this.props.item;
    let content;
    const firstItem = date ? true : false;
    content = (reservation)
      ? this.props.renderItem(reservation, firstItem)
      : content = this.props.renderEmptyDate(date);

    if (reservation)
      return (
        <View style={this.styles.container}>
          {this.renderTitle(date, reservation)}
          <View style={{flex:1}}>
            {content}
          </View>
        </View>
      );
    return (
      <View></View>
    )
  }
}

export default ReservationListItem;
