import {StyleSheet, Dimensions} from 'react-native';
import * as defaultStyle from '../../style';
import {DeviceType, DeviceModel, DeviceTypeIs} from '../../../../../src/helpers'

const STYLESHEET_ID = 'stylesheet.agenda.list';

export default function styleConstructor(theme = {}) {
  const appStyle = {...defaultStyle, ...theme};
  // i don't which is for which
  var left = getLeftAlignment()
  return  StyleSheet.create({
    container: {
      flexDirection: 'column'
    },
    dayNum: {
      fontSize: 28,
      fontWeight: '200',
      color: appStyle.agendaDayNumColor
    },
    dayText: {
      fontSize: 22,
      fontWeight: '600',
      color: appStyle.agendaDayTextColor,
      marginTop: -5,
      textAlign: 'right',
      left: left, // toheeb - date alignment
      backgroundColor: 'rgba(0,0,0,0)',
      width: '100%',
      alignItems: 'center'
    },
    day: {
      width: 500,
      justifyContent: 'center',
      marginTop: 32
    },
    today: {
      color: appStyle.agendaTodayColor
    },
    ...(theme[STYLESHEET_ID] || {})
  });
}

function getLeftAlignment() {
  if (DeviceTypeIs(DeviceModel.IPHONE_PLUSX)) { // plus
    return -104
  }
  else if (DeviceTypeIs(DeviceModel.IPHONE_REGULAR)) { // regular
     return -147
  }
  else if (DeviceTypeIs(DeviceModel.IPHONE_BABY)) {
    return -197
  }
  else if (DeviceTypeIs(DeviceModel.IPAD_9POINT7)) {
    return 250
  }
  else if (DeviceTypeIs(DeviceModel.IPAD_10POINT5)) {

  }
  else if (DeviceTypeIs(DeviceModel.IPAD_12POINT9)) {
    
  }
  else { // baby
    return  -197
  }
}

